    private static <T, R> Object pharseResultJson(String json, Gson gson, Class<T> clz, TypeToken token) throws Exception {
        JSONObject jsonObject = new JSONObject(json);
        //删除json中Response节点
        JSONObject responseObject = jsonObject.getJSONObject("Response");

        if (!responseObject.isNull("Body")) {
            if (responseObject.get("Body") instanceof JSONObject) {
                JSONObject bodyObject = responseObject.getJSONObject("Body");
                if (bodyObject.has("Items")) {
                    JSONObject itemsObject = bodyObject.getJSONObject("Items");
                    return JsonUtil.toListObject(itemsObject, "Item", clz, token.getType(), gson);
                }
            }
        }
        return null;
    }

